import *as e from "three";

export function setupMockupScene(o, t) {
    let n = new e.Color(8108249), i = new e.Color(0), r = new e.Color("blue"), l = n.clone().lerp(r, .2).lerp(i, .25),
        $ = n.clone().lerp(r, .2);
    o.background = new e.Color(l);
    let a = new e.PlaneGeometry(200, 200), s = new e.MeshStandardMaterial({color: "white"}), d = new e.Mesh(a, s);
    d.rotation.x = -Math.PI / 2, d.position.z = 96.5, d.position.y = -15, o.add(d);
    let p = new e.PlaneGeometry(200, 40), w = new e.MeshStandardMaterial({color: "white"}), c = new e.Mesh(p, w);
    c.position.set(0, 5, -3.5), o.add(c);
    let h = new e.BoxGeometry(24, 8, 8), M = new e.Mesh(h, s);
    M.position.set(0, -11, .5), o.add(M);
    let x = new e.DirectionalLight($, .5);
    x.position.set(5, 10, 5), x.target.position.set(0, 0, -10), o.add(x), o.add(x.target);
    let g = new e.DirectionalLightHelper(x, 2);
    if (o.add(g), t) {
        t.position.set(0, 0, c.position.z);
        let m = new e.AxesHelper(25);
        m.position.set(0, 0, c.position.z), o.add(m)
    }
}